   app.factory('Session',function(){
        
        return{
              addItem:function(itemName,itemValue)
              {
                  sessionStorage[itemName]= JSON.stringify(itemValue);
              },
              getItem: function(itemName)
              {
                  return JSON.parse(sessionStorage[itemName]);
              },
              removeItem: function(itemName)
              {
                  sessionStorage.removeItem(itemName);
              }

        }
   });

   app.factory('DataContext',function(filterFilter, orderByFilter){
   	   var dbActive=false;
   	   var db;
       
       //Función que verifica si la base de datos está activa en licalStarage
       function checkDb(callback){
           if(!dbActive)
           {
             var dbJson= localStorage.getItem("GComicsDB");
             if(dbJson == undefined)
             {
                 localStorage["GComicsDB"] = JSON.stringify(initialDb);
                 db=initialDb;
                 dbActive=true;
             }
             else{
                db= JSON.parse(dbJson);
                dbActive=true;
             }
          }
           callback();
       };
       
       //Función que impacta los cambios de la base de datos en memoria 'db' al localStorage
       function submitChanges(callback){
           try{
              localStorage["GComicsDB"] = JSON.stringify(db);
              callback(true);
           }catch(err){
              callback(false);
           }
       };
       
       function getIndex(array, field, value,callback)
       {
           for (var i = 0; i < array.length; i++) {
              if(array[i][field]== value)
              {
                index=i;
                callback(index);
                break;
              }
           };
       }

       //Obtener una tabla por nombre 
       function getTable(tableName)
       {
           var table;
           checkDb(function(){
                table=db[tableName];
           });
           return table;
       }
       //Obtener un registro de una tabla por nombre y expresión de filtro, uso filterFilter para hacerlo fácil
       //quien llama construye la expresión de filtro que necesita, el filtro es estricto.
       function getTableItem(tableName, expresion)
       {
           var tableItem;
           checkDb(function(){
                tableItem=filterFilter(db[tableName],expresion,true);
           });
           return tableItem;
       }
       //Inserta un nuevo registro en una tabla
       function insertTableItem(tableName, item)
       {
           var result=false;
           checkDb(function(){
               db[tableName].push(item);
               submitChanges(function(submitResult){result=submitResult});
           });
           return result;
       }
       function deleteTableItem(tableName, idField, idValue)
       {
           var result=false;
           checkDb(function(){
               getIndex(db[tableName],idField,idValue,function(index){
                  db[tableName].splice(index,1);
                  submitChanges(function(submitResult){result=submitResult});
               });
           });
           return result;
       }
       function modifyTableItem(tableName, idField, item)
       {
           var result=false;
           checkDb(function(){
               getIndex(db[tableName],idField,item[idField],function(index){
                  //db[tableName].splice(index,1,item);
                  db[tableName][index]= item;
                  submitChanges(function(submitResult){result=submitResult});
               });
           });
           return result;
       }
       
       function lastInsertId(tableName,idField)
       {
          var lastId;
           checkDb(function(){
                var arrFiltered=orderByFilter(db[tableName],idField,true);
                lastId= arrFiltered[0][idField];
           });
           return lastId;
       }

   	   return{
   	   	    checkDb: checkDb,
   	   	    getTable:getTable,
            getTableItem:getTableItem,
            insertTableItem:insertTableItem,
            deleteTableItem:deleteTableItem,
            modifyTableItem:modifyTableItem,
            lastInsertId:lastInsertId
   	   }
   });


